function writeSucc() {
    setTimeout(function() {
        err.innerHTML = 'none'
    },2500)
    setTimeout(function() {
        diagnostics.innerHTML = 'success :__ user found'
    },2600)
}

function clearErr() {
    err.innerHTML = '_'
    diagnostics.innerHTML = '_'
}

function reset() {
    user.innerHTML = '_'
    tel.innerHTML = '_'
    user_email.innerHTML = '_'
    user_password.innerHTML = '_'
    err.innerHTML = '_'
    diagnostics.innerHTML = '_'
    name.value = ''
    controlerpassword.value = ''
    visit_text.style.display = 'none'
}

resetBtn.onclick = function() {
    reset()
}

